import { useEffect, useState, forwardRef, useImperativeHandle } from "react"
import { fetchAllRole, deleteRole } from "../../services/roleService"
import { toast } from "react-toastify";

const TableRole = forwardRef((props, ref) => {

    const [listRoles, setListRoles] = useState([]);

    useEffect(() => {
        getAllRoles();
    }, [])

    useImperativeHandle(ref, () => ({

        async fetchListRolesAgain() {
            await getAllRoles();
        }

    }));

    const getAllRoles = async () => {
        let data = await fetchAllRole();
        if (data && +data.EC === 0) {
            setListRoles(data.DT)
        }
    }

    const handleDeleteRole = async (role) => {
        let data = await deleteRole(role);
        if (data && data.EC === 0) {
            toast.success(data.EM);
            await getAllRoles();
        }
    }



    return (
        <>
            <table className="table table-bordered table-hover  table-striped">
                <thead>
                    <tr>
                        <th scope="col">Id</th>
                        <th scope="col">URL</th>
                        <th scope="col">Description</th>
                        <th scope="col">Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {listRoles && listRoles.length > 0 ?
                        <>
                            {listRoles.map((item, index) => {
                                return (
                                    <tr key={`row-${index}`}>
                                        <td>{item.id}</td>
                                        <td>{item.url}</td>
                                        <td>{item.description}</td>
                                        <td>
                                            {/* <span
                                                title="Edit"
                                                className="edit"
                                                onClick={() => {
                                                    handleEditUser(item)
                                                }}
                                            >
                                                <i className="fa fa-pencil"></i>
                                            </span> */}
                                            <span
                                                title="Delete"
                                                className="delete"
                                                onClick={() => {
                                                    handleDeleteRole(item)
                                                }}

                                            ><i className="fa fa-trash-can"></i></span>
                                        </td>
                                    </tr>
                                )
                            })}
                        </>
                        :
                        <>
                            <tr><td colSpan={4}>Not found Role</td></tr>
                        </>}
                </tbody>
            </table>
        </>
    )
})
export default TableRole;
